mapper.select:
SELECT a.* FROM (SELECT rownum rn, g.* FROM game g where g_dele_status = 0
and g_title like '%' || #{keyword} || '%'
) a WHERE rn BETWEEN #{start} AND #{end}

	SELECT *
	FROM (   
    		SELECT rownum rn, a.*
    		FROM 
    			(select g.*, l.lg_title , go.go_order_date
            	 from   game g, 
                        (SELECT g_num, LISTAGG(lg_title,', ')WITHIN GROUP(ORDER BY lg_title) as lg_title
                         FROM learn_grp
                         GROUP BY g_num) l , game_order go
            	 where  g.g_num = l.g_num
            	 and	g.g_num = go.g_num
            	 and    go.m_num = ${m_num}
            	 
			 	 and 	g_title like '%' || #{keyword} || '%'
			 	 
			    ) a
		 )
	WHERE rn BETWEEN #{start} and #{end}

SELECT 	a.*
FROM   (SELECT rownum rn, g.*
		FROM  game g
		
	 	where g_title like '%' || #{keyword} || '%'
	 	
	 	
	 	and g_dele_status = #{g_dele_status}
	 	
		) a
WHERE rn BETWEEN #{start} AND #{end}

SELECT count(*) FROM game
WHERE g_title like '%' || #{keyword} || '%'
AND g_dele_status = #{g_dele_status}

 		SELECT 	*
		FROM	game
 		WHERE	g_num = #{g_num}
 		AND     m_num = #{m_num}

mapper.update:
UPDATE 	game	
SET		g_title = #{g_title}, g_step = #{g_step}, g_period = #{g_period}, g_to = #{g_to}, g_price = #{g_price}, g_content = #{g_content},g_attach_name = #{g_attach_name}, g_dele_status = #{g_dele_status}
WHERE	g_num = #{g_num}
AND     m_num = #{m_num}
