mapper.select:
	SELECT count(*) FROM homework
	WHERE m_num = #{m_num}
	
		AND h_title = #{h_title}

		SELECT DISTINCT h_title 
        FROM homework
        WHERE m_num = #{m_num}

SELECT *
FROM(
		SELECT rownum rn, hw.* 
        FROM (SELECT * 
              FROM homework
              WHERE m_num = #{m_num}
              
				 AND h_title = #{h_title}
			  
              ORDER BY h_regi_date desc ) hw
     )
WHERE rn BETWEEN #{start} AND #{end}
