mapper.select:
SELECT g.g_attach_name, lg.lg_title, m.m_name, lg.lg_sdate, lg.lg_edate, lg.lg_to, lg.lg_num, (lg.lg_to - (count(lj.lgj_approval))) remainningTo From game g, learn_grp lg, member m, lg_join lj where lg.g_num = g.g_num and lg.m_num = m.m_num and lg.lg_num = lj.lg_num and TO_CHAR(SYSDATE, 'YYYY-MM') BETWEEN (SELECT TO_CHAR(ROUND((TO_DATE(lg_edate, 'YYYY-MM') - TO_DATE(lg_sdate, 'YYYY-MM')) / 2) + TO_DATE(lg_sdate, 'YYYY-MM'), 'YYYY-MM') FROM learn_grp lg_inner WHERE lg_inner.lg_num = lj.lg_num) AND TO_CHAR(TO_DATE(lg.lg_edate, 'YYYY-MM'), 'YYYY-MM')
and lg.lg_title = #{lg_title}
group by g.g_attach_name, lg.lg_title, m.m_name, lg.lg_sdate, lg.lg_edate, lg.lg_to, lg.lg_num ORDER BY TO_DATE(lg.lg_sdate, 'YYYY-MM')
